import classes from './NewsletterSignup.module.css';
import { useFetcher } from 'react-router-dom';

function NewsletterSignup() {
    const fetcher = useFetcher()
    
  return (
    //Capital F triggers action from route, but the issue is that this component is used in main navigation, so that would be a lot of redundant code, instead we use fetcher which has its own form, which will trigger the action still but not initialize. So it will trigger, without navigating
    <fetcher.Form method="post" className={classes.newsletter} action="newsletter">
      <input
        type="email"
        placeholder="Sign up for newsletter..."
        aria-label="Sign up for newsletter"
      />
      <button>Sign up</button>
    </fetcher.Form>
  );
}

export default NewsletterSignup;
